// 79. Word Search

// 回溯 TLE
func exist1(board [][]byte, word string) bool {
	for a := 0; a < len(board); a++ {
		for b := 0; b < len(board[a]); b++ {
			got := findWord1(board, &word, string(board[a][b]), [2]int{a, b}, [][2]int{{a, b}})
			if got {
				return true
			}
		}
	}
	return false
}

func findWord1(board [][]byte, word *string, curWord string, lastPos [2]int, passed [][2]int) (got bool) {
	if got || curWord == *word {
		return true
	}
	if !strings.Contains(*word, curWord) {
		return false
	}
	a, b := lastPos[0], lastPos[1]

	for _, p := range [4][2]int{{a, b - 1}, {a - 1, b}, {a, b + 1}, {a + 1, b}} {
		x, y := p[0], p[1]

		if x >= 0 && x < len(board) && y >= 0 && y < len(board[a]) && !hasPassed(passed, [2]int{x, y}) {
			got = findWord1(board, word, curWord+string(board[x][y]), [2]int{x, y}, append(passed, [2]int{x, y}))
			if got {
				return
			}
		}
	}
	return false
}

func hasPassed(passed [][2]int, pos [2]int) bool {
	for _, v := range passed {
		if v[0] == pos[0] && v[1] == pos[1] {
			return true
		}
	}
	return false
}

// 参考评论，优化剪枝判断
func exist2(board [][]byte, word string) bool {
	chars := []byte(word)
	for a := 0; a < len(board); a++ {
		for b := 0; b < len(board[a]); b++ {
			if findWord2(board, chars, a, b, 0) {
				return true
			}
		}
	}
	return false
}

func findWord2(board [][]byte, word []byte, a, b int, i int) bool {
	if i == len(word) {
		return true
	}
	if a < 0 || b < 0 || a >= len(board) || b >= len(board[a]) {
		return false
	}
	if board[a][b] != word[i] {
		return false
	}
	// tmp := board[a][b]
	board[a][b] += 58
	got := findWord2(board, word, a-1, b, i+1) || findWord2(board, word, a, b-1, i+1) ||
		findWord2(board, word, a+1, b, i+1) || findWord2(board, word, a, b+1, i+1)
	board[a][b] -= 58
	return got
}

// 基本思想还是我的方法，用一个二维切片记录走过的路径，空间复杂度大一点，
// 用字符串拼接比较会TLE，要比较索引位的字节
func exist(board [][]byte, word string) bool {
	passed := make([][]bool, len(board))
	for i := range board {
		passed[i] = make([]bool, len(board[i]))
	}

	for a := 0; a < len(board); a++ {
		for b := 0; b < len(board[a]); b++ {

			passed[a][b] = true
			if findWord(board, &word, a, b, passed, 0) {
				return true
			}
			passed[a][b] = false
		}
	}
	return false
}

func findWord(board [][]byte, word *string, a, b int, passed [][]bool, i int) bool {
	if (*word)[i] != board[a][b] {
		return false
	}
	if i == len(*word)-1 {
		return true
	}
	for _, v := range [4][2]int{{a - 1, b}, {a, b - 1}, {a + 1, b}, {a, b + 1}} {
		x, y := v[0], v[1]
		if x >= 0 && y >= 0 && x < len(board) && y < len(board[a]) && !passed[x][y] {
			passed[x][y] = true
			if findWord(board, word, x, y, passed, i+1) {
				return true
			}
			passed[x][y] = false
		}
	}
	return false
}

